import React, { useEffect, useMemo } from "react";
import { Image, StyleSheet, Text, View } from "react-native";
import theme from "../../design/theme";
import style from "../../design/style";
import { SafeAreaView, useSafeAreaInsets } from "react-native-safe-area-context";
import { TouchableOpacity } from "react-native-gesture-handler";
import navigationService from "../../navigation/navigation-service";
import AppRoutes from "../../utils/app-routes";
import CustomText from "../text";
import CommandBus from "../../infrastructure/command-bus/command-bus";

const LayoutView = ({
    children,
    alignItems,
    justifyContent,
    showHeader = true,
    bottomSafeAreaColor = theme.colors.primary,
    bottomSafeArea = false,
    topSafeArea = true,
    customHeader,
    loading
}) => {
    const insets = useSafeAreaInsets();

    const containerStyle = useMemo(() => {
        return {
            ...styles.container,
            alignItems: alignItems,
            justifyContent: justifyContent,
        }
    }, [alignItems, justifyContent]);

    const goToProfile = () => navigationService.navigate(AppRoutes.Profile.name);

    const safeAreaEdges = useMemo(() => {
        var edges = [];
        if (topSafeArea) edges.push("top");
        if (bottomSafeArea) edges.push("bottom");
        return edges;
    }, [topSafeArea, bottomSafeArea]);

    useEffect(() => {
        if (loading) {
            CommandBus.sc.loading
        } else {
            navigationService.closeLoading();
        }
    }, [loading])

    return (
        <SafeAreaView style={containerStyle} edges={safeAreaEdges}>
            {!customHeader && showHeader &&
                <View style={styles.header}>
                    <View style={{ width: 35 }} />
                    <CustomText color={theme.colors.primary} upper h3>{global.userInfo?.name + " " + global.userInfo?.lastName}</CustomText>
                    <TouchableOpacity onPress={goToProfile}>
                        <Image source={global.userInfo?.image ? { uri: global.userInfo?.image } : require("../../../assets/logo.png")} style={styles.headerLogo} />
                    </TouchableOpacity>
                </View>
            }
            {customHeader}
            {children}
            {bottomSafeArea &&
                <View style={{ backgroundColor: bottomSafeAreaColor, height: insets.bottom, marginBottom: -insets.bottom }} />
            }
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: theme.colors.background,
    },
    header: {
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "center",
        paddingHorizontal: style.appHorizontalPadding,

    },
    headerLogo: {
        width: 35,
        height: 35,
        borderRadius: 50,
        alignSelf: "center",
        marginVertical: 10,
        borderColor: theme.colors.primary,
        borderWidth: 2.5,
    },
});

export default LayoutView;
