import React from "react"
import { Text } from "react-native"
import style from "../../design/style";
import theme from "../../design/theme";
const CustomText = ({
    children,
    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    bold,
    fontSize,
    color,
    marginTop,
    marginBottom,
    marginLeft,
    marginRight,
    fontWeight,
    dark,
    light,
    mb5,
    mb10,
    mb15,
    mb20,
    mt5,
    mt10,
    mt15,
    mt20,
    ml5,
    ml10,
    ml15,
    ml20,
    mr5,
    mr10,
    mr15,
    mr20,
    textLeft,
    textRight,
    textCenter,
    upper,
    lower

}) => {
    let _fontSize = style.fonts.normal.fontSize;
    let _fontWeight = style.fonts.normal.fontWeight;
    let _color = theme.fonts.defaultTextColor;
    let _textAlign = style.fonts.normal.textAlign;

    if (h1) {
        _fontSize = style.fonts.h1.fontSize;
        _fontWeight = style.fonts.h1.fontWeight;
        _color = style.fonts.h1.color;
        _textAlign = style.fonts.h1.textAlign;
    }
    if (h2) {
        _fontSize = style.fonts.h2.fontSize;
        _fontWeight = style.fonts.h2.fontWeight;
        _color = style.fonts.h2.color;
        _textAlign = style.fonts.h2.textAlign;
    }
    if (h3) {
        _fontSize = style.fonts.h3.fontSize;
        _fontWeight = style.fonts.h3.fontWeight;
        _color = style.fonts.h3.color;
        _textAlign = style.fonts.h3.textAlign;
    }
    if (h4) {
        _fontSize = style.fonts.h4.fontSize;
        _fontWeight = style.fonts.h4.fontWeight;
        _color = style.fonts.h4.color;
        _textAlign = style.fonts.h4.textAlign;
    }
    if (h5) {
        _fontSize = style.fonts.h5.fontSize;
        _fontWeight = style.fonts.h5.fontWeight;
        _color = style.fonts.h5.color;
        _textAlign = style.fonts.h5.textAlign;
    }
    if (h6) {
        _fontSize = style.fonts.h6.fontSize;
        _fontWeight = style.fonts.h6.fontWeight;
        _color = style.fonts.h6.color;
        _textAlign = style.fonts.h6.textAlign;
    }

    if (bold)
        _fontWeight = 'bold';

    if (fontSize)
        _fontSize = fontSize;

    if (color)
        _color = color;

    if (fontWeight)
        _fontWeight = fontWeight;

    if (dark)
        _color = theme.fonts.defaultTextColorDark;

    if (light)
        _color = theme.fonts.defaultTextColorLight;

    if (mb5)
        marginBottom = 5;

    if (mb10)
        marginBottom = 10;

    if (mb15)
        marginBottom = 15;

    if (mb20)
        marginBottom = 20;

    if (mt5)
        marginTop = 5;

    if (mt10)
        marginTop = 10;

    if (mt15)
        marginTop = 15;

    if (mt20)
        marginTop = 20;

    if (ml5)
        marginLeft = 5;

    if (ml10)
        marginLeft = 10;

    if (ml15)
        marginLeft = 15;

    if (ml20)
        marginLeft = 20;

    if (mr5)
        marginRight = 5;

    if (mr10)
        marginRight = 10;

    if (mr15)
        marginRight = 15;

    if (mr20)
        marginRight = 20;

    if (textLeft)
        _textAlign = 'left';

    if (textRight)
        _textAlign = 'right';

    if (textCenter)
        _textAlign = 'center';

    return <Text
        style={{
            fontSize: _fontSize,
            fontWeight: _fontWeight,
            color: _color,
            textAlign: _textAlign,
            marginTop,
            marginBottom,
            marginLeft,
            marginRight,
        }}
    >
        {upper ? children?.toUpperCase() : lower ? children?.toLowerCase() : children}
    </Text>;



}

export default CustomText;
