import { useSelector } from "react-redux";

export const useUserInfo = () => {
    const userInfo = useSelector(state => state.auth);
    return userInfo;
}

export const useIsLogin = () => {
    const userInfo = useSelector(state => state.auth);
    return !!userInfo?.token;
}
