import React, { useEffect, useState } from "react";
import { Text, View } from "react-native"
import { useGetApi } from "./infrastructure/api-calls/api-call"

const Root = () => {
    const [random, setRandom] = useState(Math.random())

    const userInfoResult = useGetApi({ controller: "user", action: "userInfo", reFetch: random > 0.5 });
    useEffect(() => {
        var _interval = setInterval(() => {
            setRandom(Math.random())
        }
            , 1000)
        return () => {
            clearInterval(_interval)
        }
    }, [])

    return (
        <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
            {userInfoResult.isLoading && <Text>Loading...</Text>}
            {userInfoResult.isSuccess && <Text>sa {random} sa</Text>}
            {userInfoResult.isError && <Text>{userInfoResult.error.message}</Text>}
        </View>
    )
}

export default Root
