import { useQuery } from 'react-query';
import axios from 'axios';
import ApiTypeEnum from './api-type-enum';
import general from '../../utils/general';
import i18next from 'i18next';
import ApiStatusEnum from '../../utils/enums/api-status-enum';
import AsyncStorage from '@react-native-async-storage/async-storage';
import AppConfig from '../../config/app-config';
import CommandBus from '../command-bus/command-bus';

export type ApiCallPayload = {
    uri?: string;
    controller: string;
    action?: string | null;
    itemId?: string;
    status?: string;
    data?: any;
    query?: any;
    token?: string;
    method: string;
    axiosOptions?: any;
    enabled?: boolean;
    refetchOnWindowFocus?: boolean;
    showAlertOnError?: boolean;
}

export type Result = {
    success?: boolean;
    error?: boolean;
    data?: any;
    errorMessage?: string;
    pagination?: any;
    status?: number;
}


const makeApiCall = async (payload: ApiCallPayload): Promise<Result> => {


    try {
        payload.data = { ...payload?.data, isMobile: true };

        let sendData = general.isNullOrEmpty(payload.data) ? null : { ...payload.data };

        let url = '';

        if (!general.isNullOrEmpty(payload.uri))
            url = payload.uri;
        else {
            `${AppConfig.API_URL}/${payload.controller}`;
            if (!general.isNullOrEmpty(payload.action))
                url += `/${payload.action}`;
            if (!general.isNullOrEmpty(payload.itemId))
                url += `/${payload.itemId}`;
            if (!general.isNullOrEmpty(payload.status))
                url += `/${payload.status}`;
            if (payload.query instanceof Object) {
                const keys = Object.keys(payload.query);
                if (keys.length > 0) url += "?";

                for (let i = 0; i < keys.length; ++i) {
                    url += keys[i] + "=" + ((payload.query[keys[i]] instanceof Object || payload.query[keys[i]] instanceof Array) ? encodeURI(JSON.stringify(payload.query[keys[i]])) : encodeURI(payload.query[keys[i]]));
                    if (i < keys.length - 1)
                        url += "&";
                }
            }
        }

        let options = {
            url,
            headers: {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + payload.token,
            },
            method: payload.method,
            ...payload.axiosOptions
        };


        if (payload?.axiosOptions?.data == undefined && payload.method.toLowerCase() != "get")
            options.data = sendData;
        const res = await axios(options);

        if (res.data.status === ApiStatusEnum.Success) {
            return {
                success: true,
                data: res.data.data,
                pagination: res.data.pagination
            }
        }
        if (res.data.status === ApiStatusEnum.Error) {
            if (payload.showAlertOnError && res.status != 401)
                CommandBus.sc.alertError(i18next.t(res.data.exceptionMessage));


            return {
                error: true,
                data: res.data.data,
                errorMessage: res.data.exceptionMessage,
                status: res.data.status
            }
        }

        if (payload.showAlertOnError)
            CommandBus.sc.alertError(i18next.t("general.unhandled_error") + " status: " + res.data.status);


        return {
            error: true,
            data: res.data.data,
            errorMessage: i18next.t("general.unhandled_error") + " status: " + res.data.status,
            status: res.data.status
        }

    } catch (error) {
        if (error?.response?.status === 401) {
            await AsyncStorage.removeItem("token").then((result) => {
                // RNRestart.Restart();
            }).catch((err) => {
                console.log("token remove error");
            })
        }
        if (!general.isNullOrEmpty(error?.response?.data?.status)) { // error, but not network error. Have api error

            if (payload.showAlertOnError)
                CommandBus.sc.alertError(i18next.t(error.response.data.exceptionMessage));

            return {
                error: true,
                data: error.response.data.data,
                errorMessage: error.response.data.exceptionMessage,
                status: error.response.data.status
            }
        }

        if (payload.showAlertOnError)
            CommandBus.sc.alertError(i18next.t(error?.message ?? error.response.data.exceptionMessage ?? "general.unhandled_error"));

        return {
            error: true,
            data: null,
            errorMessage: i18next.t(error?.message ?? error.response.data.exceptionMessage ?? "general.unhandled_error"),
            status: error?.response?.status
        };
    }
};

const useApiCall = (payload: ApiCallPayload) => {
    return useQuery<Result, Error>([ApiTypeEnum.API_CALL], () => makeApiCall({
        ...payload,
        showAlertOnError: payload.showAlertOnError ?? true
    }), {
        enabled: payload.enabled,
        refetchOnWindowFocus: payload.refetchOnWindowFocus
    });
};


// GET isteği
const useGetApi = (payload: ApiCallPayload) => {
    return useQuery<Result, Error>(ApiTypeEnum.GET, () => makeApiCall({
        ...payload,
        method: 'GET'
    }), {
        enabled: payload.enabled,
        refetchOnWindowFocus: payload.refetchOnWindowFocus
    });
};

// POST isteği
const usePostApi = (payload: ApiCallPayload) => {
    return useQuery<Result, Error>(ApiTypeEnum.POST, () => makeApiCall({
        ...payload,
        method: 'POST'
    }), {
        enabled: payload.enabled,
        refetchOnWindowFocus: payload.refetchOnWindowFocus
    });
};

// List isteği
const useListApi = (payload: ApiCallPayload) => {
    return useQuery<Result, Error>(ApiTypeEnum.LIST, () => makeApiCall({
        ...payload,
        action: null,
        method: "GET",
    }), {
        enabled: payload.enabled,
        refetchOnWindowFocus: payload.refetchOnWindowFocus
    });
};

// Detail isteği
const useDetailApi = (payload: ApiCallPayload) => {
    return useQuery<Result, Error>(ApiTypeEnum.DETAIL, () => makeApiCall({
        ...payload,
        action: null,
        itemId: payload.itemId,
        method: "GET",
    }), {
        enabled: payload.enabled,
        refetchOnWindowFocus: payload.refetchOnWindowFocus
    });
};

// Create isteği
const useCreateApi = (payload: ApiCallPayload) => {
    return useQuery<Result, Error>(ApiTypeEnum.CREATE, () => makeApiCall({
        ...payload,
        action: null,
        method: "POST",
    }), {
        enabled: payload.enabled,
        refetchOnWindowFocus: payload.refetchOnWindowFocus
    });
};

// Update isteği
const useUpdateApi = (payload: ApiCallPayload) => {
    return useQuery<Result, Error>(ApiTypeEnum.UPDATE, () => makeApiCall({
        ...payload,
        action: null,
        itemId: payload.itemId,
        method: "PUT",
    }), {
        enabled: payload.enabled,
        refetchOnWindowFocus: payload.refetchOnWindowFocus
    });
};

// Delete isteği
const useDeleteApi = (payload: ApiCallPayload) => {
    return useQuery<Result, Error>(ApiTypeEnum.DELETE, () => makeApiCall({
        ...payload,
        action: null,
        itemId: payload.itemId,
        method: "DELETE",
    }), {
        enabled: payload.enabled,
        refetchOnWindowFocus: payload.refetchOnWindowFocus
    });
};

const useI18n = (language: string) => {
    return useQuery<Result, Error>(ApiTypeEnum.GET, () => makeApiCall({
        controller: "wwwroot",
        action: "translates/" + (language ?? "en") + ".json",
        method: "GET",
    }), {
        enabled: true,
        refetchOnWindowFocus: false,
        retry: false,
        refetchInterval: false,
    });
}

export {
    useApiCall,
    useGetApi,
    usePostApi,
    useListApi,
    useDetailApi,
    useCreateApi,
    useUpdateApi,
    useDeleteApi,
};
