import React, { useEffect, useState } from "react";
import { Text, View } from "react-native"
import { useGetApi } from "./infrastructure/api-calls/api-call"
import AsyncStorage from "@react-native-async-storage/async-storage";
import general from "./utils/general";

const Root = () => {
    const [isPendingUserInfo, setIsPendingUserInfo] = useState(true)
    const userInfoResult = useGetApi({ controller: "user", action: "userInfo", condition: isPendingUserInfo })

    useEffect(() => {
        if (userInfoResult.isFetched) {
            setIsPendingUserInfo(false)
            console.log(userInfoResult)
            if (userInfoResult.isSuccess && userInfoResult.data.success) {
                general.setUserInfo(userInfoResult.data.data)
            }
            else if (userInfoResult.isError || userInfoResult.data.error) {
                console.log(userInfoResult.error?.message || userInfoResult.data.errorMessage)
            }
        }
    }, [userInfoResult])

    return (
        <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
            {userInfoResult.isLoading && <Text>Loading...</Text>}
            {userInfoResult.isSuccess && <Text>sa sa</Text>}
            {userInfoResult.isError && <Text>{userInfoResult.error.message}</Text>}
        </View>
    )
}

export default Root
