// NavigationService.js

import { CommonActions, StackActions, } from '@react-navigation/native';
import { DrawerActions } from "@react-navigation/native"
let _navigator = null;

let nextNavigationTime = Date.now();

function setTopLevelNavigator(navigatorRef) {
    _navigator = navigatorRef;
}

function navigate(routeName, params) {
    if (_navigator != null)
        _navigator.dispatch(
            CommonActions.navigate(
                routeName,
                params,
            )
        );
}

function clearStackAndNavigate(routeName, params = {}) {
    if (_navigator != null)
        _navigator.dispatch(
            CommonActions.reset({
                index: 0,
                routes: [
                    { name: routeName, params: params }
                ],
            })
        );
}

function push(routeName, params = {}) {
    if (Date.now() >= nextNavigationTime && _navigator != null) {
        nextNavigationTime = Date.now() + 500;
        _navigator.dispatch(
            StackActions.push(
                routeName,
                params,
            )
        );
    }
}

function goBack() {
    if (_navigator != null)
        _navigator.dispatch(
            StackActions.pop()
        );
}

function openDrawer() {
    if (_navigator != null)
        _navigator.dispatch(
            DrawerActions.openDrawer()
        );
}

function closeDrawer() {
    if (_navigator != null)
        _navigator.dispatch(
            DrawerActions.closeDrawer()
        );
}

function toggleDrawer() {
    if (_navigator != null)
        _navigator.dispatch(
            DrawerActions.toggleDrawer()
        );
}

function getParams() {
    if (_navigator != null)
        return _navigator.getCurrentRoute().params;
    return null;
}

// add other navigation functions that you need and export them

export default {
    getParams,
    navigate,
    push,
    goBack,
    openDrawer,
    closeDrawer,
    setTopLevelNavigator,
    toggleDrawer: toggleDrawer,
    clearStackAndNavigate,
    currentNavigator: _navigator
};
