import { useSelector } from "react-redux";

export const useUserInfo = () => {
    const userInfo = useSelector(state => state.auth.userInfo);
    console.log("userInfo changed", userInfo)
    return userInfo;
}

export const useIsLogin = () => {
    const userInfo = useSelector(state => state.auth.userInfo);
    return !!userInfo?.token;
}
