import React, { useEffect, useState } from "react";
import { View } from "react-native"
import { useGetApi } from "./infrastructure/api-calls/api-call"

const Root = () => {
    const userInfoResult = useGetApi({ controller: "user", action: "userInfo" });
    useEffect(() => {
        if (userInfoResult.isSuccess) {
            console.log(userInfoResult)
        }
    }, [userInfoResult])

    return (
        <View>
            {userInfoResult.isLoading && <Text>Loading...</Text>}
            {userInfoResult.isSuccess && <Text>{userInfoResult.data.data.name}</Text>}
            {userInfoResult.isError && <Text>{userInfoResult.error.message}</Text>}
        </View>
    )
}

export default Root
