import { useNavigation } from "@react-navigation/native";
import React, { useMemo } from "react";
import {
  Platform,
  StyleSheet,
  TouchableOpacity,
  View,
} from "react-native";
import AppRoutes from "../../utils/app-routes";
import general from "../../utils/general";
import theme from "../../design/theme";
import { FontAwesome } from '@expo/vector-icons';
import { useSafeAreaInsets } from "react-native-safe-area-context";
import { useQueryClient } from "react-query";

const Footer = () => {
  const navigation = useNavigation();
  const insets = useSafeAreaInsets();
  const queryClient = useQueryClient();

  let activeScreen = queryClient.getQueryData('currentPage');

  const FOOTER_ITEMS = useMemo(() => {
    const home = AppRoutes.Home.initialRoute;

    return [
      {
        screenName: home,
        navigate: home,
        icon: 'home',
      },
    ];
  }, [activeScreen]);


  if (general.isNullOrEmpty(activeScreen))
    activeScreen = AppRoutes.Home.initialRoute;

  const footerIcon = ({ icon, screenName = false, navigate }) => {
    const active = screenName === activeScreen;
    return (
      <View
        style={active ? styles.activeContainer : null}
        key={screenName}
      >
        <TouchableOpacity
          activeOpacity={1}
          onPress={() => {
            navigation.navigate(navigate);
          }}
          style={active ? styles.activeButton : null}
        >
          {active &&
            <>
              <View style={styles.activeCenterItem} />
              <View style={styles.activeLeftItem} />
              <View style={styles.activeRightItem} />
            </>
          }
          <View style={active ? styles.activeIcon : styles.inactiveIcon}>
            <FontAwesome name={icon} size={active ? 30 : 35} color={active ? "#fff" : theme.colors.white} />
          </View>
        </TouchableOpacity>
      </View>
    );
  };

  const menuContainerStyle = {
    ...styles.card,
    paddingBottom: Platform.OS === "ios" ? insets.bottom : insets.bottom + 10
  };

  return (
    <>
      <View style={menuContainerStyle}>
        {FOOTER_ITEMS.map((item, index) => footerIcon(item))}
      </View>
    </>
  );
};

const styles = StyleSheet.create({
  card: {
    backgroundColor: theme.colors.primary,
    flexDirection: "row",
    justifyContent: "space-around",
    alignItems: "center",
    paddingHorizontal: 8,
    zIndex: 0,
  },
  inactiveIcon: {
    paddingTop: 16,
    height: 60,
    width: 60,
    justifyContent: "center",
    alignItems: "center",
  },
  activeContainer: {
    width: 120,
    height: 60,
    alignItems: "center",
    overflow: "hidden",
    zIndex: 1,
  },
  activeButton: {
    backgroundColor: theme.colors.background,
    width: 60,
    height: 60,
    alignItems: "center",
    justifyContent: "center",
    borderBottomLeftRadius: 60,
    borderBottomRightRadius: 60,
  },
  activeRightItem: {
    top: 0,
    right: -60,
    position: "absolute",
    width: 60,
    height: 60,
    borderRadius: 60,
    backgroundColor: theme.colors.primary,
  },
  activeLeftItem: {
    top: 0,
    left: -60,
    position: "absolute",
    width: 60,
    height: 60,
    borderRadius: 60,
    backgroundColor: theme.colors.primary,
  },
  activeCenterItem: {
    top: 0,
    left: -30,
    position: "absolute",
    width: 120,
    height: 30,
    backgroundColor: theme.colors.background
  },
  activeIcon: {
    borderRadius: 50,
    height: 50,
    width: 50,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: theme.colors.primary,
  },
});

export default React.memo(Footer);
