import { NavigationContainer } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import React, { useCallback } from "react";
import Auth from "./shared/auth";
import SharedScreens from "./shared/shared-screens";
import TabMasterStack from "./tab";
import useUserInfo, { useObserveQuery } from "../utils/hooks/use-userinfo";
import { useQueryClient } from 'react-query';
import navigationService from "./navigation-service";
import AppRoutes from "../utils/app-routes";
import useRefState from "../utils/hooks/use-ref-state";
import { Text } from "react-native";
const Navigation = ({ initialRoute }) => {
  const [isLogin, isLoginRef, setIsLogin] = useRefState(false);
  const [nav, navRef, setNav] = useRefState(null);
  const queryClient = useQueryClient();

  const onChangeState = useCallback((state) => {
    const currentPage = navRef.current?.getCurrentRoute()?.name;
    queryClient.setQueryData('currentPage', currentPage);
  }, []);
  const Stack = createNativeStackNavigator();
  useObserveQuery("token", (token) => {
    console.log("token changed");
  });
  return (
    <NavigationContainer
      onStateChange={onChangeState}
      ref={(ref) => {
        navigationService.setTopLevelNavigator(ref);
        setNav(ref);
      }}
    >
      <Stack.Navigator
        screenOptions={{ headerShown: false }}
        initialRouteName={initialRoute}
      >
        {isLogin
          ? <Stack.Screen name={AppRoutes.Home.name} component={TabMasterStack} />
          : <Stack.Screen name={AppRoutes.Auth.name} component={Auth} />
        }
        {SharedScreens}
      </Stack.Navigator>
    </NavigationContainer>
  );
};

export default Navigation;
