import React, { useCallback } from 'react';
import { View, Text, Image, StyleSheet } from 'react-native';
import theme from '../../design/theme';
import CustomInput from '../../components/input';
import style from '../../design/style';
import CustomButton from '../../components/button';
import navigationService from '../../navigation/navigation-service';
import AppRoutes from '../../utils/app-routes';
import useRefState from '../../utils/hooks/use-ref-state';
import Validator from '../../infrastructure/validator';
import useReRender from '../../utils/hooks/use-re-render';
import AsyncStorage from '@react-native-async-storage/async-storage';
import CustomText from '../../components/text';
import LayoutView from '../../components/layout-view';
import { usePostApi } from '../../infrastructure/api-calls/api-call';

const Login = () => {
    const [formProps, formPropsRef, setFormProps] = useRefState({});
    const reRender = useReRender();
    const [validator] = useRefState(new Validator());
    const validatorScopeKey = validator.generateNewScopeKey();

    const updateFormProps = (values) => setFormProps(curr => ({ ...curr, ...values }));

    const onChange = (key) => (value) => updateFormProps({ [key]: value });

    const goToRegister = () => navigationService.push(AppRoutes.Auth.childs.Register.name)

    const goToHome = () => navigationService.navigate(AppRoutes.Auth.childs.Home.name)

    const loginResult = usePostApi({
        controller: "user",
        action: "login",
        data: formPropsRef.current,
        showAlertOnError: true,
        condition: formProps.isFormValid,
    })

    const login = useCallback(() => {
        const isValid = validator.allValid();
        reRender();
        if (isValid) {
            updateFormProps({ isFormValid: true, loading: true })
            // dispatch(ApiCallActions.NativePost({
            //     controller: "user",
            //     action: "login",
            //     data: formPropsRef.current,
            //     showAlertOnError: true,
            //     showLoading: true,
            //     onSuccess: async ({ data }) => {
            //         global.token = data.token;
            //         AsyncStorage.setItem("token", data.token);
            //         global.userInfo = data
            //         dispatch(AuthActions.setAuthState({
            //             userInfo: data,
            //             token: data.token,
            //         }));
            //     },
            //     callback: () => updateFormProps({ loading: false })
            // }))
        }
    }, []);


    return (
        <LayoutView
            showHeader={false}
            alignItems='center'
            justifyContent='space-around'
        >
            <View>
                <Image style={styles.logo} resizeMode='cover' source={require('../../../assets/logo.png')} />
                <CustomText h1>Pet App</CustomText>
            </View>
            <View style={style.w100}>

                <CustomInput
                    value={formProps.email}
                    label={'E-posta'}
                    onChange={onChange('email')}
                    errorMessage={validator.registerDestructuring({ name: `email`, value: formProps?.email, rules: [{ rule: "isEmail" }, { rule: "required" }], validatorScopeKey })}
                />
                <CustomInput
                    label={'Şifre'}
                    secureTextEntry
                    value={formProps.password}
                    onChange={onChange('password')}
                    errorMessage={validator.registerDestructuring({ name: `password`, value: formProps?.password, rules: [{ rule: "required" }], validatorScopeKey })}
                />

                <CustomButton flex={0} loading={formProps.loading} title={'Giriş Yap'} color={theme.colors.background} backgroundColor={theme.colors.primary} onPress={login} />
                <CustomButton flex={0} title={'Hesabın yok mu? Kaydol'} backgroundColor={'transparent'} color={theme.colors.primary} onPress={goToRegister} />
                <CustomButton flex={0} loading={formProps.loading} title={'Hayvanlara Göz At'} backgroundColor={theme.colors.secondary} onPress={goToHome} />
            </View>
        </LayoutView>
    );
};

const styles = StyleSheet.create({
    logo: {
        borderRadius: 100,
        overflow: 'hidden',
        width: 200,
        height: 200,
        marginBottom: 20
    },
    appName: {
        // Styles for app name
    },
    input: {
        // Styles for input fields
    },
    signupText: {
        // Styles for signup text
    },
});

export default Login;
