import { Alert } from "react-native";
import moment from "moment";
import AppRoutes from "./app-routes";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useQueryClient } from "react-query";

const isNullOrEmpty = (value) =>
  value === "" ||
  value === null ||
  value === undefined ||
  value === "undefined" ||
  (value instanceof Array && value?.length === 0);

const apiDateFormatToUserFormat = (date, showTime) => {
  if (isNullOrEmpty(date)) return null;
  if (isNaN(Date.parse(date))) return null;
  var parsedDate = new Date(date);
  if (parsedDate === NaN) return null;
  if (showTime) return moment(parsedDate).format("DD.MM.YYYY HH:mm");
  else return moment(parsedDate).format("DD.MM.YYYY");
};

const apiTimeFormatToUserFormat = (date) => {
  if (isNullOrEmpty(date)) return null;
  if (isNaN(Date.parse(date))) return null;
  var parsedDate = new Date(date);
  if (parsedDate === NaN) return null;
  return moment(parsedDate).format("HH:mm");
};

const debounce = (func, delay) => {
  let debounceTimer;
  return function () {
    const context = this;
    const args = arguments;
    clearTimeout(debounceTimer);
    debounceTimer = setTimeout(() => func.apply(context, args), delay);
  };
};

const isNumber = (value) => matchRegexp(value, /^\d+$/);

const isFloat = (value) => {
  const isFloatx = matchRegexp(value, /^(?:-?[1-9]\d*|-?0)?(?:\.\d+)?$/i);
  return isFloatx;
};

const matchRegexp = (value, regexp) => {
  const validationRegexp =
    regexp instanceof RegExp ? regexp : new RegExp(regexp);
  return validationRegexp.test(value);
};

const PhoneRegex = /\d{1}((\(\d{3}\) ?)|(\d{3})\s) ?\d{3} \d{2} \d{2}/g;
const PhoneMask = "9(999) 999 99 99";
const IdentityNoMask = "99999999999";

const alertError = (val, header = "general.error") => {
  Alert.alert(header, val);
};

const alertWarning = (val) => {
  Alert.alert("Dikkat!", val);
};

const alertSuccess = (val) => {
  Alert.alert("İşlem Başarılı!", val);
};

const alertQuest = ({ message, onClickNo, onClickYes, yesText }) => {
  Alert.alert(
    "Dikkat!",
    message,
    [
      {
        text: "Hayır",
        style: "default",
        onPress: () => {
          if (onClickNo instanceof Function) onClickNo();
        },
      },
      {
        text: yesText ?? "Evet",
        onPress: () => {
          if (onClickYes instanceof Function) onClickYes();
        },
        style: "destructive",
      },
    ],
    { cancelable: false }
  );
};

const generateRandomString = (length = 15) => {
  var result = "";
  var characters =
    "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
  var charactersLength = characters.length;
  for (var i = 0; i < length; i++) {
    result += characters.charAt(Math.floor(Math.random() * charactersLength));
  }
  return result;
};

const addDaysToDate = (date, day) => {
  date.setDate(date.getDate() + day);
  return date;
};

const isLoggedIn = () => !isNullOrEmpty(global?.token);

const minMaxStringLength = ({ min, max }) => [
  { rule: "minStringLength", value: min },
  { rule: "maxStringLength", value: max },
];

const getFileExtension = (fileName) =>
  fileName.substr(fileName.lastIndexOf(".") + 1)?.toLowerCase();

const getFileName = (fileName) =>
  fileName.substr(fileName.lastIndexOf("/") + 1);

const isImageExtension = (extension) =>
  ["jpg", "jpeg", "png"].includes(extension?.toLowerCase());

const toUpper = (text) => {
  var string = text;
  var letters = { i: "İ", ş: "Ş", ğ: "Ğ", ü: "Ü", ö: "Ö", ç: "Ç", ı: "I" };
  if (typeof text !== "string") return text;
  string = string.replace(/(([iışğüçö]))/g, function (letter) {
    return letters[letter];
  });
  return string.toUpperCase();
};

const getLocalISOString = (date) => {
  const offset = date.getTimezoneOffset();
  const offsetAbs = Math.abs(offset);
  const isoString = new Date(date.getTime() - offset * 60 * 1000).toISOString();
  return `${isoString.slice(0, -1)}${offset > 0 ? "-" : "+"}${String(
    Math.floor(offsetAbs / 60)
  ).padStart(2, "0")}:${String(offsetAbs % 60).padStart(2, "0")}`;
};

const formatNumber = (number, isPrice = false) => {
  var newNumber = "";
  if (isNullOrEmpty(number)) return newNumber;

  number = removeFormatNumber(number.toString(), isPrice);

  var moneyPart1 = number;
  var moneyPart2 = "";
  var moneyParts = number?.toString().replace(".", ",").split(",");
  if (moneyParts.length > 1) {
    moneyPart1 = moneyParts[0];
    moneyPart2 = moneyParts[1];
  }

  removeFormatNumber(moneyPart1.toString(), isPrice)
    .split("")
    .reverse()
    .forEach((x, i) => {
      if (i % 3 === 0 && i !== 0) newNumber += "." + x;
      else newNumber += x;
    });

  newNumber = newNumber.split("").reverse().join("");

  if (moneyPart2) {
    newNumber = newNumber + "," + moneyPart2;
  }
  return newNumber;
};

const removeFormatNumber = (number, isPrice) => {
  return number
    ?.toString()
    ?.replace(/\./g, "")
    ?.replace(/\,/g, ".")
    .replace(isPrice ? "₺" : "", "");
};

const replaceAll = (str, match, replacement) => {
  var escapeRegExp = match?.replace(/[.*+?^${}()|[\]\\]/g, "\\$&");
  return str?.replace(new RegExp(escapeRegExp, "g"), () => replacement);
};

const toUrl = (text) => {
  if (isNullOrEmpty(text)) {
    return text;
  }
  text = text.toLowerCase();
  text = replaceAll(text, "(", "");
  text = replaceAll(text, "i̇", "i");
  text = replaceAll(text, "^", "");
  text = replaceAll(text, "%", "");
  text = replaceAll(text, "=", "");
  text = replaceAll(text, ")", "");
  text = replaceAll(text, ":", "");
  text = replaceAll(text, "/", "");
  text = replaceAll(text, "?", "");
  text = replaceAll(text, "#", "");
  text = replaceAll(text, "[", "");
  text = replaceAll(text, "]", "");
  text = replaceAll(text, "@", "");
  text = replaceAll(text, "*", "");
  text = replaceAll(text, ".", "-");
  text = replaceAll(text, ",", "");
  text = replaceAll(text, '"', "");
  text = replaceAll(text, "&", "");
  text = replaceAll(text, "'", "");
  text = replaceAll(text, "ö", "o");
  text = replaceAll(text, "ş", "s");
  text = replaceAll(text, "ı", "i");
  text = replaceAll(text, "ü", "u");
  text = replaceAll(text, "ç", "c");
  text = replaceAll(text, "ğ", "g");
  text = replaceAll(text, "'", "");
  text = replaceAll(text, ";", "");
  text = replaceAll(text, "!", "");
  text = replaceAll(text, "’", "");
  text = replaceAll(text, " ", "-");
  text = replaceAll(text, "--", "-");

  return text;
};

const hideFooter = (screenName) => {
  return (
    screenName == AppRoutes.SharedScreens.Filter.name ||
    screenName == AppRoutes.SharedScreens.PetDetail.name
  );
}

const setUserInfo = (user) => {
  console.log("ishere");
  console.log("ishere2");
  global.userInfo = user;
  console.log("ishere3");
  if (user?.token) {
    console.log("ishere4");
    AsyncStorage.setItem("token", user.token);
    console.log("ishere5");
    console.log("ishere6");
    console.log("token setted");
  }
  else {
    console.log("ishere7");
    AsyncStorage.removeItem("token");
    console.log("ishere8");
    console.log("ishere9");
    console.log("token removed");
  }
  console.log("ishere10");
}

export default {
  getLocalISOString,
  toUpper,
  isImageExtension,
  getFileExtension,
  apiTimeFormatToUserFormat,
  isLoggedIn,
  minMaxStringLength,
  alertQuest,
  isNullOrEmpty,
  addDaysToDate,
  apiDateFormatToUserFormat,
  generateRandomString,
  alertError,
  alertWarning,
  alertSuccess,
  toUrl,
  isNumber,
  isFloat,
  matchRegexp,
  debounce,
  formatNumber,
  removeFormatNumber,
  replaceAll,
  getFileName,
  hideFooter,
  setUserInfo
};
