import { useQuery, useQueryClient } from 'react-query';
import AsyncStorage from '@react-native-async-storage/async-storage';

const useUserInfo = () => {
    const queryClient = useQueryClient();
    const checkToken = async () => {
        const token = queryClient.getQueryData('token') ?? await AsyncStorage.getItem("token");
        console.log("token", queryClient.getQueryData('token'));
        return !!token;
    };

    const { data: isLogin } = useQuery('isLogin', checkToken);

    return isLogin;
};

export default useUserInfo;
