        })();
    }, [])

    return (
        <>
            <StatusBar
                hidden={false}
                backgroundColor="#fff"
                barStyle="dark-content"
            />
            <Navigation initialRoute={AppRoutes.Auth.initialRoute} />
            <AlertRenderer />
        </>
    )
}

export default Root
