import { View, FlatList, RefreshControl } from "react-native"
import style from "../../design/style";
import CustomInput from "../../components/input";
import useRefState from "../../utils/hooks/use-ref-state";
import navigationService from "../../navigation/navigation-service";
import AppRoutes from "../../utils/app-routes";
import { useMemo } from "react";
import CustomButton from "../../components/button";
import theme from "../../design/theme";
// import PetListItem from "../../components/pet-list-item";
import useCommandBusListener from "../../infrastructure/command-bus/hooks/use-command-bus-listener";
import { CommandTypeEnum } from "../../infrastructure/command-bus/command-type-enum";
import general from "../../utils/general";
import CustomText from "../../components/text";
import LayoutView from "../../components/layout-view";

const _defaultFilterProps = {
    showNotAdopted: true,
};

const Home = ({ navigation }) => {
    const [formProps, formPropsRef, setFormProps] = useRefState({
        petList: [],
    });
    const [filterProps, filterPropsRef, setFilterProps] = useRefState(_defaultFilterProps);
    const updateFormProps = (values) => setFormProps(curr => ({ ...curr, ...values }));
    const updateFilterProps = (values) => setFilterProps(curr => ({ ...curr, ...values }));

    const onChange = (key) => (value) => updateFilterProps({ [key]: value });
    const onChangePetType = (petType) => () => updateFilterProps({ petType });

    const onRefresh = () => updateFilterProps({ updateFilter: Date.now() });

    const pushToFilter = () => navigationService.push(AppRoutes.SharedScreens.Filter.name, {
        currentFilter: filterPropsRef.current,
        saveFilter: (values) => {
            updateFilterProps(values);
        },
        clearFilter: () => {
            setFilterProps(_defaultFilterProps);
        }
    });

    const searchRightAction = useMemo(() =>
        <CustomButton
            onPress={pushToFilter}
            icon={'filter'}
            marginBottom={0}
            backgroundColor="transparent"
            color={theme.colors.primary}
            flex={0}
        />
        , []);

    useCommandBusListener(CommandTypeEnum.RefreshPetList, onRefresh);

    const customHeader = useMemo(() => {
        if (!general.isNullOrEmpty(global?.userInfo?.id)) return null;
        const customHeaderStyle = {
            zIndex: 1,
            ...style.rowBetweenCenter,
            width: '100%',
            marginBottom: -15,
        };
        return (
            <View style={customHeaderStyle}>
                <CustomButton iconSize={25} fontSize={24} color={theme.colors.primary} icon={'chevron-left'} backgroundColor="transparent" flex={0} onPress={() => navigationService.goBack()} />
                <CustomText bold textCenter color={theme.colors.primary} fontSize={20} mb15>Henüz Giriş Yapmadınız</CustomText>
                <View style={{ width: 50 }} />
            </View>
        )
    }, [])

    return (
        <LayoutView
            showHeader={!general.isNullOrEmpty(global?.userInfo?.id)}
            customHeader={customHeader}
        >
            <View style={style.container}>
                <CustomInput
                    icon={'search'}
                    label="Sahiplenmek için arama yapın"
                    rightAction={searchRightAction}
                    onChange={onChange('searchText')}
                    value={filterProps.searchText}
                />



                {/* <FlatList
                    refreshControl={<RefreshControl refreshing={formProps.loading} onRefresh={onRefresh} />}
                    data={formProps.petList}
                    renderItem={PetListItem}
                    keyExtractor={item => item.id}
                /> */}
            </View>
        </LayoutView>
    );
}

export default Home;
