import React, { useMemo } from 'react';
import { TouchableOpacity, Text, StyleSheet, View, ActivityIndicator } from 'react-native';
import theme from '../../design/theme';
import style from '../../design/style';
import general from '../../utils/general';
import { FontAwesome } from '@expo/vector-icons';
import CustomText from '../text';

const CustomButton = ({
    title,
    color = theme.button.color,
    backgroundColor = theme.button.backgroundColor,
    borderRadius = style.appBorderRadius,
    onPress,
    fontSize = style.fonts.button.fontSize,
    loading = false,
    disabled = false,
    icon,
    marginBottom = style.itemMarginBottom,
    flex = 1,
    iconSize = 20,
    marginLeft,
    marginRight,
    marginTop,
}) => {

    const buttonStyle = useMemo(() => ({
        ...styles.button,
        marginLeft,
        marginRight,
        marginTop,
        marginBottom,
        backgroundColor,
        borderRadius,
        flex,
        justifyContent: loading ? 'space-between' : 'center',
    }), [backgroundColor, borderRadius, marginBottom, flex, marginLeft, marginRight, marginTop, loading]);

    return (
        <TouchableOpacity
            style={buttonStyle}
            onPress={onPress}
            disabled={disabled || loading}
        >
            {loading &&
                <View style={styles.sideItem}>
                    {<ActivityIndicator size="small" color={color} />}
                </View>
            }
            {!general.isNullOrEmpty(title) &&
                <CustomText color={color} fontSize={fontSize}>{title}</CustomText>
            }
            {!general.isNullOrEmpty(icon) &&
                <FontAwesome name={icon} color={color} size={iconSize} />
            }
            {loading &&
                <View style={styles.sideItem}>
                    {<ActivityIndicator size="small" color={color} />}
                </View>
            }
        </TouchableOpacity>
    );
};

const styles = StyleSheet.create({
    button: {
        marginHorizontal: style.appHorizontalPadding,
        marginBottom: style.itemMarginBottom,
        padding: 10,
        alignItems: 'center',
        justifyContent: 'space-between',
        flexDirection: 'row',
    },
    sideItem: {
        width: 20,
        height: 20,
    },
});

export default CustomButton;
