const primaryColor = '#2a5c60';
const secondaryColor = '#f5a623';
const backgroundColor = '#F5F4F4';
const defaultTextColorDark = '#4f4f4f';
const defaultTextColorLight = '#bababa';
const defaultTextColor = '#878787';

const Theme = {
    colors: {
        primary: primaryColor,
        secondary: secondaryColor,
        background: backgroundColor,
        errorColor: '#bb0000',
        white: '#ffffff',
    },
    fonts: {
        titleColor: defaultTextColor,
        defaultTextColor: defaultTextColor,
        defaultTextColorDark: defaultTextColorDark,
        defaultTextColorLight: defaultTextColorLight,
    },
    button: {
        color: '#ffffff',
        backgroundColor: primaryColor,
    },
    input: {
        color: defaultTextColor,
        backgroundColor: '#ffffff',
    },
}

export default Theme;
