import React, { useEffect, useState } from "react";
import { StatusBar, Text, View } from "react-native"
import { useGetApi, useI18n } from "./infrastructure/api-calls/api-call"
import general from "./utils/general";
import Navigation from "./navigation";
import AppRoutes from "./utils/app-routes";
import AlertRenderer from "./components/alert-renderer/alertRenderer";
import i18next from "i18next";
import useRefState from "./utils/hooks/use-ref-state";
import FullScreenLoading from "./components/loading/full-screen-loading";
import CommandBus from "./infrastructure/command-bus/command-bus";
import { useDispatch } from "react-redux";
import { Actions as AuthActions } from "./infrastructure/redux/auth/reducers";

const Root = () => {
    const dispatch = useDispatch();
    const [currentLanguage, currentLanguageRef, setCurrentLanguage] = useRefState("en")
    const [formProps, formPropsRef, setFormProps] = useRefState({
        userInfoLoaded: false,
    });
    const fetchUserInfo = useGetApi({ controller: "user", action: "userInfo", enabled: false })
    const { data: translates, isFetched: translateIsFetched } = useI18n(currentLanguage);

    useEffect(() => {
        if (translateIsFetched && translates) {
            i18next.init({
                compatibilityJSON: 'v3',
                lng: currentLanguageRef.current,
                resources: {
                    [currentLanguageRef.current]: {
                        translation: translates
                    }
                }
            });
        }
    }, [translates, translateIsFetched])

    useEffect(() => {
        if (translateIsFetched && translates)
            (async () => {
                var result = await fetchUserInfo.refetch();
                if (result.isSuccess) {
                    if (result.data.success) {
                        dispatch(AuthActions.logIn(result.data.data))
                    }
                    else {
                        console.log("err", result.data)
                    }
                }
            })();
    }, [translateIsFetched])

    if (translateIsFetched && translates && fetchUserInfo.isFetched)
        return (
            <>
                <StatusBar
                    hidden={false}
                    backgroundColor="#fff"
                    barStyle="dark-content"
                />
                <Navigation initialRoute={AppRoutes.Auth.initialRoute} />
                <AlertRenderer />
                <FullScreenLoading />
            </>
        )

    return null;
}

export default Root
