import { put, takeEvery } from "redux-saga/effects";
import { ActionTypes, Actions } from "./reducers";

function* setAuthState({ payload }) {
    yield put(Actions.setAuthState(payload));
}

function* logIn({ payload }) {
    yield put(Actions.setAuthState(payload));
}

function* logOut() {
    yield put(Actions.setAuthState({}));
}


export default [
    takeEvery(ActionTypes.SET_AUTH_STATE, setAuthState),
    takeEvery(ActionTypes.LOG_IN, logIn),
    takeEvery(ActionTypes.LOG_OUT, logOut),
];
