import React from 'react';
import { TextInput, StyleSheet, View, Text } from 'react-native';
import theme from '../../design/theme';
import style from '../../design/style';
import { FontAwesome } from '@expo/vector-icons';
import CustomText from '../text';

const CustomInput = ({
    value,
    onChange,
    label,
    errorMessage,
    secureTextEntry,
    rightAction,
    icon,
    keyboardType = 'default',
    multiLine = false,
    disabled,
}) => {

    const inputStyle = {
        ...styles.input,
        color: errorMessage ? theme.colors.errorColor : theme.input.color,
        backgroundColor: errorMessage ? theme.colors.errorColor + '20' : disabled ? '#00000020' : theme.input.backgroundColor,
    };
    return (
        <View style={styles.inputContainer}>
            <View style={[style.rowBetweenCenter, styles.inputRow]}>
                {icon &&
                    <View style={styles.icon}>
                        <FontAwesome name={icon} color={theme.input.color} size={20} />
                    </View>
                }
                <TextInput
                    editable={!disabled}
                    placeholderTextColor={errorMessage ? theme.colors.errorColor : theme.input.color}
                    style={inputStyle}
                    value={value}
                    onChangeText={!disabled && onChange}
                    keyboardType={keyboardType}
                    placeholder={label}
                    secureTextEntry={secureTextEntry}
                    multiline={multiLine}
                    numberOfLines={multiLine ? 4 : 1}
                />
                {rightAction}
            </View>
            {errorMessage &&
                <CustomText
                    color={style.errorText.color}
                    marginLeft={style.errorText.marginHorizontal}
                    marginRight={style.errorText.marginHorizontal}
                    marginTop={style.errorText.marginTop}
                >
                    {errorMessage}
                </CustomText>
            }
        </View>
    );
};

const styles = StyleSheet.create({
    icon: {
        width: 40,
        height: 40,
        alignItems: 'center',
        justifyContent: 'center',
    },
    inputContainer: {
        width: '100%',
        marginBottom: style.itemMarginBottom,
        paddingHorizontal: style.appHorizontalPadding,
    },
    inputRow: {
        backgroundColor: theme.input.backgroundColor,
        borderRadius: style.appBorderRadius,
    },
    input: {
        padding: 10,
        flex: 1,
        fontSize: 16,
        color: theme.input.color,
        borderRadius: style.appBorderRadius,
    },
});

export default CustomInput;
